import Axios from 'axios';

export const Login = () => {
    const [useridReg, setUserIdReg] = useState('');
    const [passwordReg, setPasswordReg] = useState('');

    /*     const handleSubmit = (e) => {
            e.preventDefault();
            console.log(userid);
        } */
    const register = () => {
        Axios.post('https://localhost3001/register', {
            username: useridReg,
            password: passwordReg
        });
    };

    return (
        <div id="auth-form-container">
            <p id="title">Welcome to the Employee Time Management System</p>
            <form id="login-form">
                <label htmlFor="userid">User ID</label>
                <input value={useridReg} onChange={(e) => setUserId(e.target.value)} type="text" id="userid" name="userid" />
                <label htmlFor="password">Password</label>
                <input value={password} onChange={(e) => setPassword(e.target.value)} type="password" id="password" name="password" />
                <button>Register</button>
            </form>
